package Oprtor;

public class Logikal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 20;
int b = 20;
int c = 30;

System.out.println(a==b && a==c);  // both condition is match in this condition  ( AND Condition )
System.out.println(a==b || a==c); //  Any 1 condition is true than its should be true . (OR condition)
System.out.println(!(a==b || a==c)); // if i put NOT operator than will get negative results ( NOT !)
	}

}
